import React, { Component, Suspense, lazy } from "react";
import { Switch, Route, } from "react-router-dom";
import { authOperations } from "./redux/auth";

import { connect } from "react-redux";
import AppBar from "./components/AppBar";

// import Phonebook from "./pages/Phonebook";
// import HomePage from "./pages/HomePage";
// import LoginPage from "./pages/LoginPage";
// import RegisterPage from "./pages/RegisterPage";

import Container from "./components/Container/Container";
import PrivateRoute from "./components/PrivateRoute";
import PublicRoute from "./components/PublicRoute";

// const HomePage = lazy(() => import("./pages/HomePage" /* webpackChunkName: "home-page" */));
// const RegisterPage = lazy(() => import("./pages/RegisterPage" /* webpackChunkName: "register-page" */));
// const LoginPage = lazy(() => import("./pages/LoginPage" /* webpackChunkName: "login-page" */));
// const Phonebook = lazy(() => import("./pages/Phonebook" /* webpackChunkName: "phonebook-page" */));

class App extends Component {
  componentDidMount() {
    this.props.onGetCurrentUser();
  }
  render() {
    return (
      <Container>
        <AppBar />
        <Suspense fallback={<p>Загруз</p>}>
          <Switch>
            <Route exact path="/" component={HomePage} />
            <PrivateRoute
              restricted
              path="/contacts"
              component={Phonebook}
              redirectTo="/login"
            />
            <PublicRoute
              path="/register"
              restricted
              component={RegisterPage}
              redirectTo="/contacts"
            />
            <PublicRoute
              path="/login"
              restricted
              component={LoginPage}
              redirectTo="/contacts"
            />
          </Switch>
        </Suspense>
      </Container>
    );
  }
}

const mapDispatchToProps = {
  onGetCurrentUser: authOperations.getCurrentUser,
};

export default connect(null, mapDispatchToProps)(App);

