import React, { Component } from 'react';
import { connect } from 'react-redux';
import contactOperations from '../../redux/phonebook/phonebook-operations';
// import contactSelectors from "../../redux/phonebook/phonebook-selectors";

const styles = {
  form: {
    display: 'flex',
  },
};

class editContactForm extends Component {
  state = {
    id: '',
    editName: '',
    editNumber: '',
  };

  componentDidMount() {
    this.setState({
      id: this.props.contactItem.id,
      editName: this.props.contactItem.name,
      editNumber: this.props.contactItem.number,
    });
  }

  handleChange = e => {
    const { name, value } = e.currentTarget;

    this.setState({
      [name]: value,
    });
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.editContact(this.state);
    // this.setState({ name: '', number: '' });
  };

  render() {
    const { editName, editNumber } = this.state;
    const { closeContact } = this.props;

    return (
      <form style={styles.form} onSubmit={this.handleSubmit}>
        <label>
          Name
          <input
            type="text"
            name="editName"
            required
            value={editName}
            onChange={this.handleChange}
          />
        </label>

        <label>
          Number
          <input
            type="tel"
            name="editNumber"
            required
            value={editNumber}
            onChange={this.handleChange}
          />
        </label>

        <button className="btn" type="submit">
          Save
        </button>
        <button className="btn" type="button" onClick={closeContact}>
          Close
        </button>
      </form>
    );
  }
}

const mapDispatchToProps = {
  editContact: contactOperations.editContact,
};

export default connect(null, mapDispatchToProps)(editContactForm);
